scriptlet.script:
currentDN = scriptletContext.get("currentDN");
currDN = scriptletContext.get("currDN");
baseDN = scriptletContext.get("baseDN");
separator = scriptletContext.get("separator");

if ( (currentDN.indexOf(baseDN) > -1) && (currDN != "") && (currDN.indexOf(baseDN) == -1) )
    scriptletContext.put("currDN", currDN + separator + currentDN);
else
    scriptletContext.put("currDN", currentDN);
