scriptlet.script:
if (typeof(scriptletContext.get("badURLs")) == "undefined" || scriptletContext.get("badURLs") == "")
    scriptletContext.put("badDelimiter", "");
else
    scriptletContext.put("badDelimiter", ";");
if (typeof(scriptletContext.get("slowURLs")) == "undefined" || scriptletContext.get("slowURLs") == "")
    scriptletContext.put("slowDelimiter", "");
else
    scriptletContext.put("slowDelimiter", ";");
if (typeof(scriptletContext.get("goodURLs")) == "undefined" || scriptletContext.get("goodURLs") == "")
    scriptletContext.put("goodDelimiter", "");
else
    scriptletContext.put("goodDelimiter", ";");

var tested = "";

var names = ["badURLs", "slowURLs", "goodURLs"];
delimiter = "";

for (var i = 0; i < names.length; i++)
    if (typeof(scriptletContext.get(names[i])) != "undefined" && scriptletContext.get(names[i]) != "") {
        tested += delimiter + scriptletContext.get(names[i]); 
        delimiter = ";";
    }

scriptletContext.put("toTest", tested);

